import React, { useState, useEffect } from 'react';
import './BackgroundSwitcher.css';

const BackgroundSwitcher = () => {
  const [currentBg, setCurrentBg] = useState(1);
  const [isOpen, setIsOpen] = useState(false);

  const backgrounds = [
    { id: 1, name: 'Misty Mountains', preview: 'https://images.unsplash.com/photo-1506905925346-21bda4d32df4?ixlib=rb-4.0.3&auto=format&fit=crop&w=300&q=80' },
    { id: 2, name: 'Forest Rain', preview: 'https://images.unsplash.com/photo-1441974231531-c6227db76b6e?ixlib=rb-4.0.3&auto=format&fit=crop&w=300&q=80' },
    { id: 3, name: 'Coastal Storm', preview: 'https://images.unsplash.com/photo-1507525428034-b723cf961d3e?ixlib=rb-4.0.3&auto=format&fit=crop&w=300&q=80' },
    { id: 4, name: 'Mountain Lake', preview: 'https://images.unsplash.com/photo-1506905925346-21bda4d32df4?ixlib=rb-4.0.3&auto=format&fit=crop&w=300&q=80' },
    { id: 5, name: 'Waterfall Valley', preview: 'https://images.unsplash.com/photo-1519904981063-b0cf448d479e?ixlib=rb-4.0.3&auto=format&fit=crop&w=300&q=80' },
    { id: 6, name: 'Desert Oasis', preview: 'https://images.unsplash.com/photo-1506905925346-21bda4d32df4?ixlib=rb-4.0.3&auto=format&fit=crop&w=300&q=80' },
    { id: 7, name: 'Aurora Borealis', preview: 'https://images.unsplash.com/photo-1519904981063-b0cf448d479e?ixlib=rb-4.0.3&auto=format&fit=crop&w=300&q=80' },
    { id: 8, name: 'Tropical Paradise', preview: 'https://images.unsplash.com/photo-1506905925346-21bda4d32df4?ixlib=rb-4.0.3&auto=format&fit=crop&w=300&q=80' }
  ];

  useEffect(() => {
    // Load saved background preference
    const savedBg = localStorage.getItem('rainyBackground');
    if (savedBg) {
      setCurrentBg(parseInt(savedBg));
      applyBackground(parseInt(savedBg));
    } else {
      applyBackground(1);
    }
  }, []);

  const applyBackground = (bgId) => {
    // Remove existing background classes
    document.body.className = document.body.className.replace(/rainy-bg-\d+/g, '');
    
    // Add new background class
    if (bgId > 1) {
      document.body.classList.add(`rainy-bg-${bgId}`);
    }
    
    // Also apply to login container if it exists
    const loginContainer = document.querySelector('.login-container');
    if (loginContainer) {
      loginContainer.className = loginContainer.className.replace(/rainy-bg-\d+/g, '');
      if (bgId > 1) {
        loginContainer.classList.add(`rainy-bg-${bgId}`);
      }
    }
  };

  const handleBackgroundChange = (bgId) => {
    setCurrentBg(bgId);
    applyBackground(bgId);
    localStorage.setItem('rainyBackground', bgId.toString());
    setIsOpen(false);
  };

  return (
    <div className="background-switcher">
      <button 
        className="switcher-toggle"
        onClick={() => setIsOpen(!isOpen)}
        title="Change Background"
      >
        🌧️
      </button>
      
      {isOpen && (
        <div className="switcher-dropdown">
          <div className="switcher-header">
            <h4>Choose Scenic Background</h4>
            <button 
              className="close-btn"
              onClick={() => setIsOpen(false)}
            >
              ×
            </button>
          </div>
          
          <div className="background-options">
            {backgrounds.map((bg) => (
              <div 
                key={bg.id}
                className={`background-option ${currentBg === bg.id ? 'active' : ''}`}
                onClick={() => handleBackgroundChange(bg.id)}
              >
                <div className="background-preview">
                  <img 
                    src={bg.preview} 
                    alt={bg.name}
                    loading="lazy"
                  />
                  <div className="preview-overlay">
                    {currentBg === bg.id && <span className="checkmark">✓</span>}
                  </div>
                </div>
                <span className="background-name">{bg.name}</span>
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
};

export default BackgroundSwitcher;
