import React, { useEffect, useRef } from 'react';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';

const RADAR_TEMPLATES = [
  // Allow override via env
  process.env.REACT_APP_RADAR_TILE_URL_TEMPLATE || '',
  // RainViewer variants (try in order)
  'https://tilecache.rainviewer.com/v2/radar/nowcast/256/{z}/{x}/{y}/2/1_1.png',
  'https://tilecache.rainviewer.com/v2/radar/nowcast_0/256/{z}/{x}/{y}/2/1_1.png',
  'https://tilecache.rainviewer.com/v2/radar/last/256/{z}/{x}/{y}/2/1_1.png'
].filter(Boolean);

const MapView = ({ routePath = [], alerts = [] }) => {
  const mapRef = useRef(null);
  const mapInstanceRef = useRef(null);
  const routeLayerRef = useRef(null);
  const markersRef = useRef([]);

  // Function to fit map to route bounds
  const fitToRoute = () => {
    if (mapInstanceRef.current && routeLayerRef.current) {
      const bounds = routeLayerRef.current.getBounds();
      mapInstanceRef.current.fitBounds(bounds, { 
        padding: [20, 20],
        maxZoom: 15,
        animate: true,
        duration: 1.0
      });
    }
  };

  useEffect(() => {
    if (!mapInstanceRef.current) {
      mapInstanceRef.current = L.map(mapRef.current, {
        center: [22.9734, 78.6569], // India approx center
        zoom: 6,
        zoomControl: true, // Enable zoom controls
        scrollWheelZoom: true, // Enable scroll wheel zoom
        doubleClickZoom: true, // Enable double-click zoom
        boxZoom: true, // Enable box zoom
        keyboard: true, // Enable keyboard navigation
        dragging: true, // Enable dragging
        touchZoom: true, // Enable touch zoom for mobile
        zoomSnap: 0.1, // Allow fractional zoom levels
        zoomDelta: 0.5, // Zoom increment
        wheelPxPerZoomLevel: 120, // Scroll sensitivity
      });

      // Add custom zoom controls with better styling
      const zoomControl = L.control.zoom({
        position: 'topright',
        zoomInText: '+',
        zoomOutText: '‚àí',
        zoomInTitle: 'Zoom in',
        zoomOutTitle: 'Zoom out'
      }).addTo(mapInstanceRef.current);

      // Add custom "Fit to Route" control
      const fitToRouteControl = L.Control.extend({
        onAdd: function(map) {
          const container = L.DomUtil.create('div', 'leaflet-bar leaflet-control leaflet-control-custom');
          container.style.backgroundColor = 'white';
          container.style.border = 'none';
          container.style.borderRadius = '8px';
          container.style.boxShadow = '0 2px 10px rgba(0, 0, 0, 0.15)';
          container.style.overflow = 'hidden';

          const button = L.DomUtil.create('button', 'fit-to-route-btn', container);
          button.innerHTML = 'üìç';
          button.style.width = '40px';
          button.style.height = '40px';
          button.style.border = 'none';
          button.style.background = 'white';
          button.style.cursor = 'pointer';
          button.style.fontSize = '16px';
          button.style.transition = 'all 0.2s ease';
          button.title = 'Fit to Route';

          button.onmouseover = function() {
            this.style.background = '#f8f9fa';
            this.style.transform = 'scale(1.05)';
          };

          button.onmouseout = function() {
            this.style.background = 'white';
            this.style.transform = 'scale(1)';
          };

          button.onclick = function() {
            fitToRoute();
          };

          L.DomEvent.disableClickPropagation(container);
          return container;
        },
        onRemove: function(map) {}
      });

      new fitToRouteControl({ position: 'topright' }).addTo(mapInstanceRef.current);

      L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
        maxZoom: 19,
        attribution: '&copy; OpenStreetMap contributors',
      }).addTo(mapInstanceRef.current);

      // Radar overlay with fallback templates
      const tryTemplates = [...RADAR_TEMPLATES];
      const addRadarFromTemplate = () => {
        if (tryTemplates.length === 0) return; // give up silently
        const tpl = tryTemplates.shift();
        const layer = L.tileLayer(tpl, {
          opacity: 0.6,
          zIndex: 400,
          tileSize: 256,
          crossOrigin: true
        });
        let anyTileLoaded = false;
        const successOnce = () => { anyTileLoaded = true; layer.off('tileload', successOnce); };
        const onErr = () => {
          // if nothing loaded yet, fallback to next template
          setTimeout(() => {
            if (!anyTileLoaded) {
              layer.remove();
              addRadarFromTemplate();
            }
          }, 200);
        };
        layer.on('tileload', successOnce);
        layer.on('tileerror', onErr);
        layer.addTo(mapInstanceRef.current);
      };
      addRadarFromTemplate();
    }

    // Clear previous layers
    if (routeLayerRef.current) {
      routeLayerRef.current.remove();
      routeLayerRef.current = null;
    }
    markersRef.current.forEach(m => m.remove());
    markersRef.current = [];

    // Draw route
    if (routePath.length > 1) {
      const latlngs = routePath.map(p => [p.lat, p.lng]);
      routeLayerRef.current = L.polyline(latlngs, { 
        color: '#4f46e5', 
        weight: 5, 
        opacity: 0.8,
        smoothFactor: 1.0 // Smoother curves
      }).addTo(mapInstanceRef.current);

      // Enhanced fit bounds with better padding and zoom calculation
      const bounds = routeLayerRef.current.getBounds();
      const boundsSize = bounds.getNorthEast().distanceTo(bounds.getSouthWest());

      // Calculate appropriate padding based on route size
      let padding = [20, 20];
      if (boundsSize > 100000) { // Large route (>100km)
        padding = [50, 50];
      } else if (boundsSize > 50000) { // Medium route (50-100km)
        padding = [30, 30];
      }

      mapInstanceRef.current.fitBounds(bounds, { 
        padding: padding,
        maxZoom: 15, // Don't zoom too close for long routes
        animate: true,
        duration: 1.0 // Smooth animation
      });

      // Add a slight delay to ensure smooth zoom
      setTimeout(() => {
        if (mapInstanceRef.current) {
          const currentZoom = mapInstanceRef.current.getZoom();
          // If zoomed too far out, set a minimum zoom level
          if (currentZoom < 8) {
            mapInstanceRef.current.setZoom(8, { animate: true });
          }
        }
      }, 100);
    }

    // Draw markers
    alerts.forEach(a => {
      if (!a.coords) return;
      const isRain = a.severity === 'high';
      const marker = L.circleMarker([a.coords.lat, a.coords.lng], {
        radius: 8,
        color: isRain ? '#dc2626' : '#16a34a',
        fillColor: isRain ? '#ef4444' : '#22c55e',
        fillOpacity: 0.9,
        weight: 3,
        className: 'weather-marker' // Add class for styling
      }).addTo(mapInstanceRef.current);

      const popupHtml = `
        <div style="min-width:220px; font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif;">
          <div style="font-size: 16px; font-weight: 600; color: #1f2937; margin-bottom: 8px;">
            ${a.location}
          </div>
          <div style="font-size: 14px; color: #374151; margin-bottom: 8px;">
            ${a.alert}
          </div>
          <div style="font-size: 12px; color: #6b7280; border-top: 1px solid #e5e7eb; padding-top: 6px;">
            ${a.weatherData ? `üå°Ô∏è ${a.weatherData.temperature}&deg;C | ${a.weatherData.description}` : 'Weather: N/A'}
          </div>
        </div>
      `;
      marker.bindPopup(popupHtml, {
        closeButton: true,
        autoClose: false,
        closeOnClick: false,
        className: 'custom-popup'
      });
      markersRef.current.push(marker);
    });
  }, [routePath, alerts]);

  return (
    <div 
      className="map-container"
      style={{ 
        height: window.innerWidth <= 768 ? '300px' : '420px', 
        width: '100%', 
        borderRadius: 12, 
        overflow: 'hidden', 
        boxShadow: '0 10px 20px rgba(0,0,0,0.1)',
        position: 'relative',
        background: '#f8f9fa',
        minHeight: '300px'
      }} 
      ref={mapRef} 
    />
  );
};

export default MapView;
